// src/App.js
import React, { useState } from "react";
import MovieCard from "./components/MovieCard";
import MovieList from "./components/MovieList";
import Filter from  "./components/Filter"
import "./App.css";

function App() {
  const [movies, setMovies] = useState([
    {
      title: "Into the BadLands",
      description: "A mighty warrior and a young boy with supernatural powers search for enlightenment in a ruthless post-apocalyptic America controlled by feudal barons.",
      posterURL: "https://upload.wikimedia.org/wikipedia/en/4/4d/Into_the_Badlands_%28TV_series%29_title.png",
      rating: 4.5,
    },

    // Add more movies here

    {
      title: "Raising Kanan ",
      description: "Kanan Stark, the character first played by executive producer Curtis 50 Cent Jackson.",
      posterURL: "data:image/jpeg;base64,",
      rating: 4.5,
    },

    {
      title: "Black Mafia Family",
      description: "Two brothers who rose from the decaying streets of southwest Detroit in the late 1980s and gave birth to one of the most influential crime families in the country.",
      posterURL: "https://www.themoviedb.org/t/p/w500/g2juyqFlVTbKqCSm6Xsh23HnBF7.jpg",
      rating: 4.8,
    },


    {
      title: "Game of Thrones",
      description: "Nine noble families fight for control over the lands of Westeros, while an ancient enemy returns after being dormant for a millennia.",
      posterURL: "data:image/jpeg;base64,",
      rating: 4.5,
    },


    {
      title: "Breaking Bad",
      description: "A chemistry teacher diagnosed with inoperable lung cancer turns to manufacturing and selling methamphetamine with a former student in order to secure his family's future.",
      posterURL: "data:image/jpeg;base64,",
      rating: 4.9,
    },


    {
      title: "Ozark",
      description: "A financial advisor drags his family from Chicago to the Missouri Ozarks, where he must launder money to appease a drug boss.",
      posterURL: "data:image/jpeg;base64,",
      rating: 4.5,
    },


    {
      title: "How to Get Away with Murder",
      description: "A group of ambitious law students and their brilliant criminal defense professor become involved in a twisted murder plot that promises to change the course of their lives.",
      posterURL: "data:image/jpeg;base64,",
      rating: 4.5,
    },



    {
      title: "Peaky Blinders",
      description: "A gangster family epic set in 1900s England, centering on a gang who sew razor blades in the peaks of their caps, and their fierce boss Tommy Shelby.",
      posterURL: "data:image/jpeg;base64,",
      rating: 4.5,
    },



  ]);


  const [titleFilter, setTitleFilter] = useState("");
  const [rateFilter, setRateFilter] = useState("");

  const handleTitleChange = (event) => {
    setTitleFilter(event.target.value);
  };

  const handleRateChange = (event) => {
    setRateFilter(event.target.value);
  };

  const filteredMovies = movies.filter(
    (movie) =>
      movie.title.toLowerCase().includes(titleFilter.toLowerCase()) &&
      (rateFilter === "" || movie.rating >= parseFloat(rateFilter))
  );

  return (
    <div className="app">
      <h1>Movie App</h1>
      
      <Filter
        titleFilter={titleFilter}
        rateFilter={rateFilter}
        onTitleChange={handleTitleChange}
        onRateChange={handleRateChange}
      />
      <MovieList movies={filteredMovies} />
    
    </div>
  );
}

export default App;









